import React, { Component } from 'react';
import './Mapresult.scss';
import {
  Map, TileLayer, Marker, Popup,
} from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const iconBase = '/medias/icone-easylunch-orange.png';

// Nous initialisons une liste de marqueurs
const restos = [
  { titre: 'Cromagnon', lat: 44.84328199999999, lon: -0.5813342000000148 },
  { titre: 'Le resto d\'Ysis', lat: 44.92419639999999, lon: -0.6231397999999899 },
  { titre: 'Le monologue', lat: 44.84287820000001, lon: -0.5816670000000386 },

];

class Mapresult extends Component {
  constructor(props) {
    super(props);
    this.state = {
      liste: [],
    };
  }

  componentDidMount() {
    this.setState({ liste: restos });
  }

  render() {
    const { liste } = this.state;
    const icone = new L.Icon({
      iconUrl: iconBase,
      iconAnchor: [25, 50],
      popupAnchor: [-3, -76],
      iconSize: new L.Point(100, 100),
    });

    return (
      <Map className="Map" center={[44.8333, -0.5667]} zoom={17}>
        <TileLayer
          attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {liste.map(resto => (
          <Marker key={resto.titre} position={[resto.lon, resto.lat]} icon={icone}>
            <Popup>{resto.titre}</Popup>
          </Marker>
        ))}
      </Map>
    );
  }
}

export default Mapresult;
