import React, { Component } from 'react';
import MapGL, { Marker, Popup, NavigationControl } from 'react-map-gl';
import {
  TabContent,
  TabPane,
  Nav,
  NavItem,
  NavLink,
  Card,
  Button,
  CardTitle,
  CardText,
  Row,
  Col,
  Popover,
  PopoverBody,
} from 'reactstrap';
import classnames from 'classnames';
import './Mapresult.scss';


const TOKEN = 'pk.eyJ1IjoiY3RyaSIsImEiOiJjanAyaXV1OGcwNzJpM3dwaDhwejJvZjJnIn0.bVruUQb_cXzaHLyWmk1zSg';

const navStyle = {
  position: 'absolute',
  top: 0,
  left: 0,
  padding: '10px',
};

class Mapresult extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeTab: '1',
      viewport: {
        latitude: 44.8333,
        longitude: -0.5667,
        zoom: 14,
        bearing: 0,
        pitch: 0,
      },
      popoverOpen: false,
    };
    this.toggle = this.toggle.bind(this);
    this.togglePin = this.togglePin.bind(this);
    this.updateViewport = this.updateViewport.bind(this);
    this.renderCityMarker = this.renderCityMarker.bind(this);
  }


  updateViewport = (viewport) => {
    this.setState({ viewport });
  }

  renderCityMarker = (resto, index) => {
    const { popoverOpen } = this.state;
    return (
      <div key={`marker-${index}`}>
        <Marker
          longitude={resto.lon}
          latitude={resto.lat}
        >
          <button id={`Popover-${resto.index}`} onClick={this.togglePin} type="button">
            <img src="medias/icone-easylunch-orange.png" alt="icone" />
          </button>
          <Popover placement="bottom" isOpen={popoverOpen} target={`Popover-${resto.index}`} toggle={this.toggle}>
            <PopoverBody>{resto.titre}</PopoverBody>
          </Popover>
        </Marker>
        <Popup latitude={resto.lat} longitude={resto.lon} closeButton closeOnClick={false} anchor="top">
          <div>{resto.titre}</div>
        </Popup>
      </div>
    );
  }

  toggle(tab) {
    const { activeTab } = this.state;
    if (activeTab !== tab) {
      this.setState({
        activeTab: tab,
      });
    }
  }

  togglePin() {
    const { popoverOpen } = this.state;
    this.setState({
      popoverOpen: !popoverOpen,
    });
  }

  render() {
    const restos = [
      { titre: 'Cromagnon', lat: 44.84328199999999, lon: -0.5813342000000148 },
      { titre: 'Le resto d\'Ysis', lat: 44.92419639999999, lon: -0.6231397999999899 },
      { titre: 'Le monologue', lat: 44.84287820000001, lon: -0.5816670000000386 },

    ];
    const {
      activeTab,
    } = this.state;
    const { viewport } = this.state;

    return (
      <div>
        <Nav tabs>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '1' })}
              onClick={() => { this.toggle('1'); }}
            >
              Carte
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '2' })}
              onClick={() => { this.toggle('2'); }}
            >
              Menu
            </NavLink>
          </NavItem>
        </Nav>
        <TabContent activeTab={activeTab}>
          <TabPane tabId="1">
            <Row>
              <Col sm="12">
                <MapGL
                  {...viewport}
                  width="100%"
                  height="650px"
                  mapStyle="mapbox://styles/mapbox/light-v9"
                  onViewportChange={this.updateViewport}
                  mapboxApiAccessToken={TOKEN}
                >

                  { restos.map((resto, index) => this.renderCityMarker(resto, index)) }

                  <div className="nav" style={navStyle}>
                    <NavigationControl onViewportChange={this.updateViewport} />
                  </div>

                </MapGL>
              </Col>
            </Row>
          </TabPane>
          <TabPane tabId="2">
            <Row>
              <Col sm="12">
                <Card body>
                  <CardTitle>Special Title Treatment</CardTitle>
                  <CardText>Text</CardText>
                  <Button>Go somewhere</Button>
                </Card>
              </Col>
            </Row>
          </TabPane>
        </TabContent>
      </div>
    );
  }
}

export default Mapresult;
