import React, { Component } from 'react';
import { Collapse, Button, Card, CardImg, Row, Col } from 'reactstrap';

class Explication extends Component {
    constructor(props) {
        super(props);
        this.toggleChoisir = this.toggleChoisir.bind(this);
        this.toggleReserver = this.toggleReserver.bind(this);
        this.togglePartager = this.togglePartager.bind(this);
        this.state = { collapseChoisir: false, collapseReserver: false, collapsePartager: false};
      }
    toggleChoisir() {
    this.setState({ collapseChoisir: !this.state.collapseChoisir });
    }
    toggleReserver() {
        this.setState({ collapseReserver: !this.state.collapseReserver });
        }
    togglePartager() {
        this.setState({ collapsePartager: !this.state.collapsePartager });
        }    
    render() {
        return (
        <Row>
            <Col xs="12" sm="3">
                <p>Utilisation en <span className="">3 étapes</span></p>
            </Col>
            <Col xs="4" sm="3">
                <Button data-toggle="collapse" data-target="#choisir" color="warning" onClick={this.toggleChoisir} style={{ marginBottom: '1rem' }}>Choisissez votre restaurant</Button>
                <Collapse id="choisir" isOpen={this.state.collapseChoisir}>
                    <Card>
                        <CardImg top width="100%" src="/medias/Choisir-un-restaurant.png" alt="Choisir un restaurant" />
                    </Card>
                </Collapse>
            </Col>
            <Col xs="4" sm="3">
                <Button data-toggle="collapse" data-target="#reserver" color="warning" onClick={this.toggleReserver} style={{ marginBottom: '1rem' }}>Réservez et passez votre commande avant 11h30</Button>
                <Collapse id="reserver" isOpen={this.state.collapseReserver}>
                <Card>
                    <CardImg top width="100%" src="/medias/Commander.png" alt="Commander" />
                </Card>
                </Collapse>
            </Col>
            <Col xs="4" sm="3">
                <Button data-toggle="collapse" data-target="#partager" color="warning" onClick={this.togglePartager} style={{ marginBottom: '1rem' }}>Partagez un moment convivial</Button>
                <Collapse id="partager" isOpen={this.state.collapsePartager}>
                <Card>
                    <CardImg top width="100%" src="/medias/Partager.png" alt="Partager" />
                </Card>
                </Collapse>
            </Col>

        </Row>
        );
    }
    }

export default Explication;
