import React, { Component } from 'react';
import { render } from 'react-dom';
import MapGL, { Marker, Popup, NavigationControl } from 'react-map-gl';

import RestoPin from '../../components/result/resto-pin';
import RestoInfo from '../../components/result/resto-info';

const TOKEN = 'pk.eyJ1IjoiY3RyaSIsImEiOiJjanAyaXV1OGcwNzJpM3dwaDhwejJvZjJnIn0.bVruUQb_cXzaHLyWmk1zSg';

const navStyle = {
  position: 'absolute',
  top: 0,
  left: 0,
  padding: '10px',
};

const restos = [
  { titre: 'Cromagnon', lat: 44.84328199999999, lon: -0.5813342000000148 },
  { titre: 'Le resto Ysis', lat: 44.92419639999999, lon: -0.6231397999999899 },
  { titre: 'Le monologue', lat: 44.84287820000001, lon: -0.5816670000000386 },

];

const avgLat = (tab) => {
  let total = 0;
  for (let i = 0; i < tab.length; i += 1) {
    total += tab[i].lat;
  }
  return total / tab.length;
};

const avgLon = (tab) => {
  let total = 0;
  for (let i = 0; i < tab.length; i += 1) {
    total += tab[i].lon;
  }
  return total / tab.length;
};

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewport: {},
      popupInfo: null,
    };
  }

  componentDidMount() {
    const tempLat = avgLat(restos);
    const tempLon = avgLon(restos);
    const tempViewport = {
      latitude: tempLat,
      longitude: tempLon,
      zoom: 14,
      bearing: 0,
      pitch: 0,
    };
    this.setState({ viewport: tempViewport });
  }


  updateViewport = (viewport) => {
    this.setState({ viewport });
  }

  renderCityMarker = (resto, index) => (
    <Marker
      key={`marker-${index}`}
      longitude={resto.lon}
      latitude={resto.lat}
    >
      <RestoPin size={36} onClick={() => this.setState({ popupInfo: resto })} />
    </Marker>
  )

  renderPopup() {
    const { popupInfo } = this.state;

    return popupInfo && (
      <Popup
        tipSize={10}
        anchor="top"
        longitude={popupInfo.lon}
        latitude={popupInfo.lat}
        onClose={() => this.setState({ popupInfo: null })}
      >
        <RestoInfo info={popupInfo} />
      </Popup>
    );
  }

  render() {
    const { viewport } = this.state;

    return (
      <MapGL
        className="Mapresult"
        {...viewport}
        width="100%"
        height="650px"
        mapStyle="mapbox://styles/mapbox/light-v9"
        onViewportChange={this.updateViewport}
        mapboxApiAccessToken={TOKEN}
      >

        { restos.map(this.renderCityMarker) }

        {this.renderPopup()}

        <div className="nav" style={navStyle}>
          <NavigationControl onViewportChange={this.updateViewport} />
        </div>

      </MapGL>
    );
  }
}

export function renderToDom(container) {
  render(<App />, container);
}
