import React, { Component } from 'react';
import mapboxgl from 'mapbox-gl';
import {
  TabContent, TabPane, Nav, NavItem, NavLink, Card, Button, CardTitle, CardText, Row, Col,
} from 'reactstrap';
import classnames from 'classnames';

class Mapresult extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeTab: '1',
      lng: -0.5667,
      lat: 44.8333,
      zoom: 14,

    };
    this.toggle = this.toggle.bind(this);
  }

  componentDidMount() {
    const { lng, lat, zoom } = this.state;
    this.map = new mapboxgl.Map({
      container: this.mapContainer,
      style: 'mapbox://styles/mapbox/light-v9',
      center: [lng, lat],
      zoom,
    });
  }

  toggle(tab) {
    const { activeTab } = this.state;
    if (activeTab !== tab) {
      this.setState({
        activeTab: tab,
      });
    }
  }

  render() {
    const restos = [
      { titre: 'Cromagnon', lat: 44.84328199999999, lon: -0.5813342000000148 },
      { titre: 'Le resto d\'Ysis', lat: 44.92419639999999, lon: -0.6231397999999899 },
      { titre: 'Le monologue', lat: 44.84287820000001, lon: -0.5816670000000386 },

    ];
    const {
      activeTab,
    } = this.state;

    const style = {
      position: 'absolute',
      top: 0,
      bottom: 0,
      width: '100%',
      height: '650px',
    };

    return (
      <div>
        <Nav tabs>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '1' })}
              onClick={() => { this.toggle('1'); }}
            >
              Carte
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '2' })}
              onClick={() => { this.toggle('2'); }}
            >
              Menu
            </NavLink>
          </NavItem>
        </Nav>
        <TabContent activeTab={activeTab}>
          <TabPane tabId="1">
            <Row>
              <Col sm="12">
                <div
                  style={style}
                  ref={(el) => {
                    this.mapContainer = el;
                  }
                  }
                />
              </Col>
            </Row>
          </TabPane>
          <TabPane tabId="2">
            <Row>
              <Col sm="12">
                <Card body>
                  <CardTitle>Special Title Treatment</CardTitle>
                  <CardText>Text</CardText>
                  <Button>Go somewhere</Button>
                </Card>
              </Col>
            </Row>
          </TabPane>
        </TabContent>
      </div>
    );
  }
}

export default Mapresult;

